sys_variable_value.value:
(function execute(inputs, outputs) {
if(inputs.role == 'current_control_scheduler'){
	outputs.role_id = '14382'
}else if(inputs.role == 'assigned_individual_s_group'){
	outputs.role_id = '13194'
}else if(inputs.role == 'controls_custodian'){
	outputs.role_id = '16276'
}else if(inputs.role == 'notification_person'){
	outputs.role_id = '14093'
}

})(inputs, outputs);

