sys_variable_value.value:
(function execute(inputs, outputs) {
	var usersid = JSON.parse(inputs.response)["RequestedObject"]["FieldContents"][inputs.roleID]["Value"];
  	var fullstring = JSON.stringify(usersid)
    if( fullstring.search(inputs.replaceduserid) != -1){
      var start = fullstring.search(inputs.replaceduserid);
      var final = fullstring.substring(start + inputs.replaceduserid.length, fullstring.length)
      var initial = fullstring.substring(0, start)
      stringDataForAutomation = initial + inputs.itcmuserid + final
      outputs.usersid = stringDataForAutomation
    }else{
      var start = fullstring.search("UserList");
      var final = fullstring.substring(start+12, fullstring.length)
      var initial = fullstring.substring(0, start+11)
      stringDataForAutomation = initial + "{"+ '"' + 'Id":'+ inputs.itcmuserid + " },{"  + final
      outputs.usersid = stringDataForAutomation
    }

})(inputs, outputs);

