sys_variable_value.value:
(function execute(inputs, outputs) {
  var start = inputs.userid.search("Id");
  var end = inputs.userid.search("DisplayName");
  outputs.itcmuserid = inputs.userid.substring(start + 4, end - 2)

  var start = inputs.replacedid.search("Id");
  var end = inputs.replacedid.search("DisplayName");
  outputs.replaceduserid = inputs.replacedid.substring(start + 4, end - 2)
})(inputs, outputs);

