sys_variable_value.value:
(function execute(inputs, outputs) {
  var start = inputs.response.search("Id");
  var end = inputs.response.search("DisplayName");
  outputs.itcmuserid = inputs.response.substring(start + 4, end - 2)
})(inputs, outputs);

