sys_variable_value.value:
(function execute(inputs, outputs) {
	var usersid = JSON.parse(inputs.response)["RequestedObject"]["FieldContents"]["10084"]["Value"];
  	var userList = usersid["UserList"]
    var stringDataForAutomation = "{"+ '"' + 'Id:' + '"'+ inputs.itcmuserid + " },"
    stringDataForAutomation = stringDataForAutomation + userList[0].toString()
	outputs.usersid = stringDataForAutomation

})(inputs, outputs);

