sys_variable_value.value:
(function execute(inputs, outputs) {
var sessionToken;
var n = inputs.responseBody.search("SessionToken");
  var m = inputs.responseBody.search("InstanceName");
if (n != -1) {
	sessionToken = inputs.responseBody.substring(n + 15, m - 3)
    outputs.sessiontoken = sessionToken
 }
})(inputs, outputs);

