sys_variable_value.value:
(function execute(inputs, outputs) {
if(inputs.role == 'author_s'){
	outputs.role_id = '12956'
}else if(inputs.role == 'owner'){
	outputs.role_id = '13602'
}else if(inputs.role == 'updater_s'){
	outputs.role_id = '16053'
}else if(inputs.role == 'reader_s'){
	outputs.role_id = '16140'
}

})(inputs, outputs);

