sys_variable_value.value:
(function execute(inputs, outputs) {

  var responseBody = JSON.parse(inputs.responseBody)["RequestedObject"];
  var authorsUser = responseBody["FieldContents"]["12956"]["Value"]["UserList"];
  var authorsGroup = responseBody["FieldContents"]["12956"]["Value"]["GroupList"];
  var stewards = responseBody["FieldContents"]["16280"]["Value"]["UserList"];

  var auxAuthorsUser = [];
  var auxAuthorsGroup = [];

  var auxStewards = [];

  outputs.recordname = responseBody["FieldContents"]["12950"]["Value"];
  outputs.owner = responseBody["FieldContents"]["13602"]["Value"]["UserList"][0]["Id"];


  for (var i = 0; i < authorsUser.length; i++) {
    auxAuthorsUser.push(authorsUser[i]["Id"]);
  }

  for (var i = 0; i < authorsGroup.length; i++) {
    auxAuthorsGroup.push(authorsGroup[i]["Id"]);
  }

  for (var i = 0; i < stewards.length; i++) {
    auxStewards.push(stewards[i]["Id"]);
  }

  outputs.authorsuser = auxAuthorsUser;
  outputs.authorsgroup = auxAuthorsGroup;
  outputs.stewards = auxStewards;

})(inputs, outputs);

