sys_variable_value.value:
(function execute(inputs, outputs) {

  // Substituir esses domínios hardcoded por algum tipo de atualização dinâmica da lista de domínios (API, banco de dados, etc)
  domains = ["No Domain"/*0*/ ,"NA"/*1*/,"UPSTREAMACCTS"/*2*/,"AP"/*3*/, "EA"/*4*/,  "SA"/*5*/, "AF"/*6*/, "XTO" /*7*/,"ACCPT"/*8*/]

  var body = JSON.parse(inputs.bodyJson)["RequestedObject"];
  if (body["DomainId"] != null && body["UserName"] != null) {
  	outputs.lanid = [domains[body["DomainId"]]+"\\"+body["UserName"] +"; "+body["DisplayName"]];
  }
  else {
  	outputs.lanid = "ERR";
  }

  return;  
})(inputs, outputs);

