sys_variable_value.value:
(function execute(inputs, outputs) {

  var responseBody = JSON.parse(inputs.responseBody)["RequestedObject"];
  var schedulersUser = responseBody["FieldContents"]["14382"]["Value"]["UserList"];
  var schedulersGroup = responseBody["FieldContents"]["14382"]["Value"]["GroupList"];
  var stewards = responseBody["FieldContents"]["16278"]["Value"]["UserList"];

  var auxSchedulersUser = [];
  var auxSchedulersGroup = [];

  var auxStewards = [];

  outputs.recordname = responseBody["FieldContents"]["13454"]["Value"];


  for (var i = 0; i < schedulersUser.length; i++) {
    auxSchedulersUser.push(schedulersUser[i]["Id"]);
  }

  for (var i = 0; i < schedulersGroup.length; i++) {
    auxSchedulersGroup.push(schedulersGroup[i]["Id"]);
  }

  for (var i = 0; i < stewards.length; i++) {
    auxStewards.push(stewards[i]["Id"]);
  }

  outputs.schedulersUser = auxSchedulersUser;
  outputs.schedulersgroup = auxSchedulersGroup;
  outputs.stewards = auxStewards;

})(inputs, outputs);

