sys_variable_value.value:
(function execute(inputs, outputs) {
var lanID = inputs.user_lanid.toUpperCase()
lanID = lanID.split('\\')
outputs.useruppercase = lanID[1]

var replaced = inputs.replaceduser.toUpperCase()
replaced = replaced.split('\\')
outputs.replaceduppercase = replaced[1]
})(inputs, outputs);

