sys_variable_value.value:
(function execute(inputs, outputs) {
if(inputs.role == 'author'){
	outputs.role_id = '10084'
}else if(inputs.role == 'owner'){
	outputs.role_id = '10866'
}else if(inputs.role == 'custodian'){
	outputs.role_id = '13338'
}

})(inputs, outputs);

